require 'rake'
module Rake
  class Application
    attr_accessor :current_task
  end
  class Task
    alias :old_execute :execute 
    def execute(args=nil)
      Rake.application.current_task = @name  
      old_execute(args)
    end
  end #class Task
end #module Rake 
class Stopwatch
    def initialize(params=nil)
        @start = Time.now
    end
    def end()
        finish = Time.now
        diff = finish - @start
        hours = (diff / 3600).to_i
        mins = (diff / 60 - hours * 60).to_i
        secs = (diff - (mins * 60 + hours * 3600)).to_i
        time=sprintf("%02d:%02d:%02d", hours, mins, secs)
        puts "time taken  for set up:  #{time}"
    end
end
class MAGEINSTALLER_Helper
    require 'fileutils'
    
    #kinda hard coded
    def add_setting(target,opts=nil)
        File.open(target, "a+") do |f|
          f.write(opts)
        end
    end

    #sudo task events
    def get_pre_task()
        task=Rake.application.current_task;
        event = "Pre_#{task}"
        self.load_event(event);
    end
    def get_post_task()
        task=Rake.application.current_task;
        event = "Post_#{task}"
        self.load_event(event);
    end
    def load_event(event)
        puts event
        file = "#{Dir.pwd}/rake/events/#{event}.rb"
        if File.exist?(file)
            load "#{file}"  
            Object::const_get(event).new
        end
    end
    
    
    #error handled file methods 
    def file_remove(file)
         File.delete(file) if File.exist?(file)
    end
    def create_dir(dir)
        dir=File.join(Dir.pwd, dir)
        if !File.exists?(dir)
            Dir.mkdir(dir, 0700) #=> 0
            puts "ceated file #{dir}\n"
        else
            puts "existing file #{dir}\n"
        end
    end  
    
    #a method to provide feedback simply 
    def download(from,to)
        #['net/http','uri'].each_with_index {|i| require i}#seems to fail on Macs
        
        require 'net/http'
        require 'uri'
        
        counter = 0

        url_base = from.split('/')[2]
        url_path = '/'+from.split('/')[3..-1].join('/')
             
        puts "starting download for #{url_path}\n"
        Net::HTTP.start(url_base) do |http|
          begin
            file = open(to, 'wb')
            file.binmode

            http.request_get(url_path) do |response|
                size = 0
                total = response.header["Content-Length"].to_f
              response.read_body do |chunk|
                    file << chunk
                    counter += 1
                    size += chunk.size
                    new_progress = (size.to_f / total.to_f * 100.0).round(2)
                    progress = "#" * (new_progress / 10)
                    print "#{progress} --#{new_progress}%--\r"
              end
            end
          ensure
            file.close
          end
        end
        puts "completed download for #{url_path}\n"
    end

    
