require 'rake'
module Rake
  class Application
    attr_accessor :current_task
  end
  class Task
    alias :old_execute :execute 
    def execute(args=nil)
      Rake.application.current_task = @name  
      old_execute(args)
    end
  end #class Task
end #module Rake 
class Stopwatch
    def initialize(params=nil)
        @start = Time.now
    end
    def end(message=nil)
        finish = Time.now
        diff = finish - @start
        hours = (diff / 3600).to_i
        mins = (diff / 60 - hours * 60).to_i
        secs = (diff - (mins * 60 + hours * 3600)).to_i
        time=sprintf("%02d:%02d:%02d", hours, mins, secs)
        if message==nil 
            puts "time taken  for set up:  #{time}"
        else
            puts "#{message} #{time}"
        end
    end
end

module JSON
  def self.is_json?(foo)
    begin
      return false unless foo.is_a?(String)
      JSON.parse(foo).all?
    rescue JSON::ParserError
      false
    end 
  end
end

module MAGEINSTALLER_Helper
    require 'fileutils'
    
    #kinda hard coded
    def add_setting(target,opts=nil)
        File.open(target, "a+") do |f|
          f.write(opts)
        end
    end

    #sudo task events
    def get_pre_task()
        task=Rake.application.current_task;
        event = "Pre_#{task}"
        self.load_event(event);
    end
    def get_post_task()
        task=Rake.application.current_task;
        event = "Post_#{task}"
        self.load_event(event);
    end
    def load_event(event)
        puts event
        file = "#{Dir.pwd}/rake/events/#{event}.rb"
        if File.exist?(file)
            load "#{file}"  
            Object::const_get(event).new
        end
    end
    
    #REFACTOR THIS LATER
    def load_settings()
        require 'json'
        file = File.open("scripts/installer_settings.json", "rb")
        contents = file.read
        file.close
        if JSON.is_json?(contents)
        #if contents.length > 5 #note this should be changed for a better content check.. ie:valid json
            begin
                parsed = JSON.parse(contents)
                @CONFIG = parsed
            rescue SystemCallError
                puts "must redo the settings file"
            else    
                puts parsed['bs_mode']
                parsed.each do |key, value|
                    #puts "#{key}=>#{value}"
                    instance_variable_set("@" + key, value)
                end
            end
        else
            puts "must redo the settings file"
        end
    end

